	clear all, close all	wcT = 0.35*pi;	 wsT = 0.67*pi; Amax = 0.1;	 Amin = 60; T = 1;	wc = (2/T)*tan(wcT/2); ws = (2/T)*tan(wsT/2);	Aminx = Amin*1.08;	N = CA_CONST_R_ORDER_S(wc, ws, Amin)	N = 7	[Ga,Za,R_ZEROS,Pa,Wsnew] = CA_CONST_R_POLES_S(wc, ws,Aminx,N);	rp = sqrt(wc*Wsnew); 	[Gd, Zd, Pd] = PZ_2_PZ_Z(Ga, Za, Pa, T);	alfa = CT_TREE_WDF_LP(Pd);		wTstopb = linspace(wsT,pi,200); 	Wceff = 13; WX = 2^Wceff;  	alfaQ = QUANTIZE_ALFA(alfa, Wceff);	load mincost;		TempCost = 10000;	a = rp^2/4	a = 1;			% Zero adder cost  	mincost((a*WX))	N0 = 20; N1 = 20; 	for m = -10:10	% Try different poles in the first-order section		alfaQ(1,1) = round(rp*WX/(rp+2)) + m;		alfaQ(1,2) = alfaQ(1,1);		alfaQ(1,3) = 2*WX - alfaQ(1,1) - alfaQ(1,2);		alfaQ(1,4) = 1;		cost1 = (N-1)*mincost(alfaQ(1,1));		for k0 = -N0:N1			alfaQ(2,4) = alfaQ(2,4)+k0;			alfaQ(2,3) = round(alfaQ(2,4)*a);			alfaQ(2,1) = WX - (alfaQ(2,3)+alfaQ(2,4))/2;			alfaQ(2,2) = alfaQ(2,1);			for k1 = -N0:N1				alfaQ(3,4) = alfaQ(3,4)+k1;				alfaQ(3,3) = round(alfaQ(3,4)*a);				alfaQ(3,1) = WX - (alfaQ(3,3)+alfaQ(3,4))/2;				alfaQ(3,2) = alfaQ(3,1);				for k2 = -N0:N1 					alfaQ(4,4) = alfaQ(4,4)+k2;					alfaQ(4,3) = round(alfaQ(4,4)*a);					alfaQ(4,1) = WX - (alfaQ(4,3)+alfaQ(4,4))/2;					alfaQ(4,2) = alfaQ(4,1);					Cost = cost1 + sum(mincost(abs(alfaQ(:,4))));					if Cost < TempCost						[s11, s21] = CT_TREE_WDF_LP_H(alfaQ/WX, wTstopb);						Att = MAG_2_ATT(s21);						if min(Att) >= Amin							%	[s11, s21] = CT_TREE_WDF_LP_H(alfaQ/WX, wTpassb);							%	Att = MAG_2_ATT(s21);							%	if min(Att) <= Amax							alfaQmin = alfaQ; TempCost = Cost							%	end						end					end				end			end		end	end	alfaQmin(1,4) = 0;	cost = TempCost	alfaQ = alfaQmin/WX	figure(1), 	wT = linspace(0, pi, 1000);	[s11, s21] = CT_TREE_WDF_LP_H(alfaQ, wT);	Att = MAG_2_ATT(s21);	PLOT_ATT_Z(wT, Att, pi, 80)	, zoom on	alfaQmin		mincost(alfaQ(1,1))	mincost(abs(alfaQmin(2:end,4)))	