	N =  16; 										% Decimation factor 	wT = linspace(eps, 0.2*pi, 1000);	alpha = 0.02;	Hc = sin(wT*N)./sin(wT)/N; 						% Comb filter frequency response 	Hpa = sin((wT+alpha)*N)./sin(wT+alpha)/N;	Hma = sin((wT-alpha)*N)./sin(wT-alpha)/N; 	H = Hpa.*Hma.*Hc; 								% Modified comb filter section	subplot('position', [0.1 0.4 0.88 0.5]);	PLOT_MAG_Z_dB(wT, H, 0.2*pi, 90, 'H')