	% Ex 14.29	close all, clear all, clc	Norder = 15; Be = [0 0.75*pi]; Dslope = [1]; W = [1];  Lorder = 0;	%	[h, Err] = EQR_FIR(Norder, Be, D, W,'sym');	%	H0 = freqz(h,1,1000);	wT = linspace(0, pi, 1000);	%	PLOT_MAGNITUDE_Z_LS(wT,abs(H0), 0, 2, 'H')		Nterms = 6;          	% Number of terms in the series	for m = 1:Nterms		Lorder = m-1;		[h_Macl(m,:), Err] = GDIFF_EQR_FIR(Norder, Be, Dslope, W, Lorder);		H_Macl(m,:) = freqz(h_Macl(m,:),1,1000)';%		figure(m)%		PLOT_MAGNITUDE_Z_LS(wT,abs(HL(m,:)), 0, 1, 'H')	%		axis([0 pi 0 1.1]);grid on%		uwrapphase = -unwrap(angle(HL(m,:)));%		wT(1) = 1; 				% Avoid to dividing by zero%		phasedelay(m,:) = -uwrapphase./wT; phasedelay(m,1) = phasedelay(m,2);%		wT(1) = 0; 	end	 	%=================================================	%	Farrow Structure	d0 = -0.5:0.1:0.5; 	H = zeros(length(d0),1000);	for k = 1:length(d0)		H0  = H_Macl(1,:);		d = d0(k);		for m = 2:Nterms			H0 = H0 +  H_Macl(m,:)*(d^(m-1)/FACTORIAL(m-1));		end   		H(k,:) = H0.*exp(-i*wT*d*0.85);		uwrapphase = -unwrap(angle(H(k,:)));		wT(1) = 1; 				% Avoid to dividing by zero		phasedelay(k,:) = -uwrapphase./wT; phasedelay(k,1) = phasedelay(k,2);		wT(1) = 0;	end	PLOT_FD_RESPONSE2(wT, H, phasedelay, length(d0), d0, Norder/2)