	% Fig 2.7 + 2.8 	clear all	Nsample = 100;	noise(1:Nsample) = randn(1,Nsample);	for k = 1:Nsample		x(k) = sin(2*pi*k/30);	end	z = x;	x = x + 0.15*noise;	for k = 1:91		y(k) = sum(x(k:k+9));	end	y = y/10;	grid on	ylable = '{\itx}({\itn})';	figure(1)	subplot('position', [0.1 0.4 0.86 0.5]);	PLOT_SEQUENCE(x, ylable)		axis([0, 100, -1.2, 1.2]);	figure(2)	ylabel ='{\ity}({\itn})';		subplot('position', [0.1 0.4 0.86 0.5]);	PLOT_SEQUENCE(y, ylabel)	axis([0, 92, -1.2, 1.2]);