	clear all	close all	N = 6; K = 200;	alfa = pi/N;	Q = 1/512;	a1 = 1.91015625;	a2 = -0.9375;	x = zeros(1, K);y1 = 0; y2 = 0;	for n = 1:30		x(n) = round(0.11*cos(alfa*n)*512)/512;		end	x(31) = -0.4;	for n = 1:K		y(n) = x(n) + a1*y1+ a2*y2;		if abs(y(n)) > 1			if y(n) >= 1 				y(n) = y(n) - 2;				%y(n) = 1 - Q;			else				y(n) = y(n) + 2;			%y(n) = -1			end		end		y2 = y1;		y1 = y(n);  		  	end	subplot(2,1,1);	stem(x, 'k.-');	  	axis([0 K -0.15 0.15]);	set(gca,'FontName','times','FontSize',16)	ylabel('{\itx}','FontName', 'times','FontSize',16);	grid on		subplot(2,1,2);	stem(y*512, 'k.-');	axis([0 K -612 611]);	set(gca,'FontName','times','FontSize',16)	ylabel('{\ity_Q}','FontName','times','FontSize',16);	grid on