	% Example 5.26	clear all	close all	clc	N  =  42;		% N must be even	L  =  8;		%  L must be divisible by 4'	woT= 0.4*pi;	% Center frequency	ws1T = 0.25*pi;	ws2T = 0.8*pi;		h = FLAT_BP_FIR_1(N,L,woT,ws1T,ws2T);	wT = linspace(0, pi, 1000);	H = freqz(h,1, wT);	subplot(2,1,1)	PLOT_MAG_Z_dB(wT, H, pi, 80, 'H')		dsu = 0.01;	dsl = -dsu;			h = FLAT_BP_FIR_2(N, L, woT, dsl, dsu);	H = freqz(h,1, wT);	subplot(2,1,2)	PLOT_MAG_Z_dB(wT, H, pi, 80, 'H')			