	function c = CAL_COEFF_SECOND(P)		% Calculates the coefficients for the second-order sections (Lattice WDF)  	% 	Toolbox for DIGITAL FILTERS USING MATLAB		% Author: 			Hakan Johansson 2004-07-17	% Modified by: 	 	% Copyright:		by authors - not released for commercial use	% Version:			1 	% Known bugs: 	% Report bugs to:	Wanhammar@gmail.com		c = 1;	index = 1;	if ~(P(1) == 1),		while index <= length(P),			c(index) = (P(index)-P(index+1)-1)/(P(index)+P(index+1)+1);			c(index+1) = (1-P(index+1))/(1+P(index+1));			index = index+2;		end	end