	function Amin = CH_Amin_Z(N, wcT, wsT, Amax)		% 	Computes Amin for a lowpass digital Chebyshevi I or II filter of order N		% 	Toolbox for DIGITAL FILTERS USING MATLAB		% 	Author: 		Tapio Saramaki, 2018-03-10	% 	Modified by: 	LW, 2018-04-04	% 	Version: 		1	% 	Known bugs:			% 	Report bugs to:	tapio.saramaki@tut.fi		if N ~= round(N)		disp([ 'Must be an integer'])		break	end	Ome = tan(wsT/2)/tan(wcT/2);    Amin = 10*log10(1+(10^(Amax/10)-1)*cosh(N*acosh(Ome))^2);	