	 function [H,P0,P1,P2,P3,P4] = LTH_BAND_14_15_H(x, ewT, eLwT)	 	 % Function for Example 14.15	 	 % 	Toolbox for DIGITAL FILTERS USING MATLAB 	 	 % 	Author: 		Lars Wanhammar 2011-02-11	 % 	Modified by: 	 	 % 	Copyright:		by authors - not released for commercial use	 % 	Version:		1	 % 	Known bugs:		 	 % 	Report bugs to:	Wanhammar@gmail.com	 	 P0 = ewT.^(-9);	 P1 = (1-x(1)*eLwT).*(1-x(2)*eLwT)./((eLwT-x(1)).*(eLwT-x(2)));	 P2 = (1-x(3)*eLwT).*(1-x(4)*eLwT)./((eLwT-x(3)).*(eLwT-x(4)))./ewT.^1;	 P3 = (1-x(5)*eLwT).*(1-x(6)*eLwT)./((eLwT-x(5)).*(eLwT-x(6)))./ewT.^2;	 P4 = (1-x(7)*eLwT).*(1-x(8)*eLwT)./((eLwT-x(7)).*(eLwT-x(8)))./ewT.^3;	 H = (P0+P1+P2+P3+P4)/5;	 