	function PLOT_ABS_Z(wT, MAG, Ylabel)		% 	Plots the magnitude given the magnitude response of a digital filter 	%	Toolbox for DIGITAL FILTERS USING MATLAB			% 	Author: 		Lars Wanhammar 2007-10-07	% 	Modified by: 	LW 2008-07-09		% 	Copyright:		by authors - not released for commercial use	% 	Version:		1			 	% 	Known bugs:		Wanhammar@gmail.com			%========================================================		% Standard settings	fs = 16; 		% Font size	lw = 2; 		% Linewidth	fn = 'times'; 	% Font	%========================================================		  %	subplot('position', [0.1 0.4 0.88 0.5]); 	plot(wT, MAG,'linewidth', 2);  	axis([0 pi 0 1]);	set(gca,'FontName', 'times','FontSize', 16);	ax = [0 0.1*pi 0.2*pi 0.3*pi 0.4*pi 0.5*pi 0.6*pi 0.7*pi 0.8*pi 0.9*pi pi];	lab = {'0';'0.1\pi';'0.2\pi';'0.3\pi';'0.4\pi';'0.5\pi';'0.6\pi';'0.7\pi';'0.8\pi';'0.9\pi';'\pi'};	if Ylabel ~= '-'		ylabel(['{|\it' Ylabel '}({\ite^j^\omega^T})|'],'FontName', fn,'FontSize',fs);	end	%	xlabel('{\it\omegaT} [rad]');		xtick(ax,lab);	grid on;	hold on